<?php$route = '/services/:service_id/funding/:funding_id/';$app->delete($route, function ($service_id,$funding_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$funding_id = filter_var($funding_id, FILTER_SANITIZE_STRING);		$Query = "DELETE FROM funding WHERE id = '" . $funding_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Funding Deleted!";	$ReturnObject['id'] = $funding_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>