<?php$route = '/services/:service_id/funding/:funding_id/';$app->get($route, function ($service_id,$funding_id)  use ($app){	$ReturnObject = array();		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$funding_id = filter_var($funding_id, FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM funding WHERE id = '" . $funding_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$source = $Database['source'];		$F = array();			$F['id'] = $funding_id;		$F['service_id'] = $service_id;		$F['source'] = $source;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>