<?php$route = '/services/:service_id/';$app->get($route, function ($service_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$Query = "SELECT * FROM service WHERE id = '" . $service_id . "'";	///echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$program_id = $Database['program_id'];		$location_id = $Database['location_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$description = $Database['description'];		$url = $Database['url'];		$email = $Database['email'];		$status = $Database['status'];		$interpretation_services = $Database['interpretation_services'];		$application_process = $Database['application_process'];		$wait_time = $Database['wait_time'];		$fees = $Database['fees'];		$accreditations = $Database['accreditations'];		$licenses = $Database['licenses'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['program_id'] = $program_id;		$F['location_id'] = $location_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['description'] = $description;		$F['url'] = $url;		$F['email'] = $email;		$F['status'] = $status;		$F['interpretation_services'] = $interpretation_services;		$F['application_process'] = $application_process;		$F['wait_time'] = $wait_time;		$F['fees'] = $fees;		$F['accreditations'] = $accreditations;		$F['licenses'] = $licenses;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>