<?php$route = '/services/:service_id/holiday-schedule/:holiday_schedule_id/';$app->delete($route, function ($service_id,$holiday_schedule_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$holiday_schedule_id = filter_var($holiday_schedule_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM holiday_schedule WHERE id = '" . $holiday_schedule_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Holiday_schedule Deleted!";	$ReturnObject['ID'] = $holiday_schedule_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>