<?php$route = '/services/:service_id/intepretation_services/:intepretation_services_id/';$app->delete($route, function ($service_id,$interpretation_services_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$interpretation_services_id = filter_var($interpretation_services_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM intepretation_services WHERE id = '" . $interpretation_services_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Intepretation_services Deleted!";	$ReturnObject['id'] = $interpretation_services_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>