<?php$route = '/services/:service_id/interpretation-services/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);			$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);		if(isset($clean['language']))		{		$interpretation_services_id = getGUID();				$language = $clean['language'];		$query = "INSERT INTO interpretation_services(";					$query .= "id,";		$query .= "service_id,";		$query .= "language";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($interpretation_services_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($language) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $interpretation_services_id;		$F['service_id'] = $service_id;		$F['language'] = $language;			$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>