<?php$route = '/services/:service_id/languages/:language_id/';$app->delete($route, function ($service_id,$language_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$language_id = filter_var($language_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM language WHERE id = '" . $language_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Language Deleted!";	$ReturnObject['id'] = $language_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>