<?php$route = '/services/:service_id/languages/:language_id/';$app->put($route, function ($service_id,$language_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$language_id = filter_var($language_id, FILTER_SANITIZE_STRING);	$language = mysql_real_escape_string($_BODY['language']);	$query = "UPDATE language SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",language='" . mysql_real_escape_string($language) . "'";	$query .= " WHERE id = '" . $language_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $language_id;		$F['service_id'] = $service_id;		$F['language'] = $language;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>