<?php$route = '/services/:service_id/locations/';$app->get($route, function ($service_id)  use ($app){	$ReturnObject = array();		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);			$Query = "SELECT * FROM location l JOIN service_at_location sal ON l.location_id = sal.location_id";	$Query .= "	WHERE sal.service_id = '" . $service_id . "'";	$Query .= " ORDER BY name ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$description = $Database['description'];		$transportation = $Database['transportation'];		$latitude = $Database['latitude'];		$longitude = $Database['longitude'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['description'] = $description;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;										array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>