<?php$route = '/services/:service_id/payment-accepted/:payment_accepted_id/';$app->delete($route, function ($service_id,$payment_accepted_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$payment_accepted_id = filter_var($payment_accepted_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM payment_accepted WHERE id = '" . $payment_accepted_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Payment Accepted Deleted";	$ReturnObject['id'] = $payment_accepted_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>