<?php$route = '/services/:service_id/payment-accepted/:payment_accepted_id/';$app->put($route, function ($service_id,$payment_accepted_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);					$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$payment_accepted_id = filter_var($payment_accepted_id, FILTER_SANITIZE_STRING);		$payment = filter_var($_BODY['payment'], FILTER_SANITIZE_STRING);	$query = "UPDATE payment_accepted SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",payment='" . mysql_real_escape_string($payment) . "'";	$query .= " WHERE id = '" . $payment_accepted_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $payment_accepted_id;	$F['service_id'] = $service_id;	$F['payment'] = $payment;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>