<?php$route = '/services/:service_id/phones/:phone_id/';$app->delete($route, function ($service_id,$phone_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$phone_id = filter_var($phone_id, FILTER_SANITIZE_STRING);		$Query = "DELETE FROM phone WHERE id = '" . $phone_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Phone Deleted";	$ReturnObject['id'] = $phone_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>