<?php$route = '/services/:service_id/regular-schedule/';$app->get($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM regular_schedule WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY weekday ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$regular_schedule_id = $Database['id'];		$location_id = $Database['location_id'];		$service_at_location_id = $Database['service_at_location_id'];				$weekday = $Database['weekday'];		$opens_at = $Database['opens_at'];		$closes_at = $Database['closes_at'];		$F = array();		$F['id'] = $regular_schedule_id;		$F['location_id'] = $location_id;		$F['service_id'] = $service_id;		$F['service_at_location_id'] = $service_at_location_id;		$F['weekday'] = $weekday;		$F['opens_at'] = $opens_at;		$F['closes_at'] = $closes_at;;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>