<?php$route = '/services/:service_id/regular-schedule/:required_document_id/';$app->delete($route, function ($service_id,$regular_schedule_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$regular_schedule_id = filter_var($regular_schedule_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM regular_schedule WHERE id = '" . $regular_schedule_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Regular_schedule Deleted";	$ReturnObject['id'] = $regular_schedule_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>