<?php$route = '/services/:service_id/required-document/';$app->get($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM required_document WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY document ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$required_document_id = $Database['id'];		$document = $Database['document'];		$F = array();			$F['id'] = $required_document_id;		$F['service_id'] = $service_id;		$F['document'] = $document;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>