<?php$route = '/services/:service_id/required-document/:required_document_id/';$app->delete($route, function ($service_id,$required_document_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$required_document_id = filter_var($required_document_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM required_document WHERE id = '" . $required_document_id . "' AND service_id = '" . $service_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Required_document Deleted!";	$ReturnObject['id'] = $required_document_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>