<?php$route = '/services/:service_id/required-document/:required_document_id/';$app->get($route, function ($service_id,$required_document_id)  use ($app){	$ReturnObject = array();		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$required_document_id = filter_var($required_document_id, FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM required_document WHERE id = '" . $required_document_id . "' AND service_id = '" . $service_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$document = $Database['document'];		$F = array();			$F['id'] = $required_document_id;		$F['service_id'] = $service_id;		$F['document'] = $document;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>