<?php$route = '/services/:service_id/required-document/:required_document_id/';$app->put($route, function ($service_id,$required_document_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);	$required_document_id = filter_var($required_document_id, FILTER_SANITIZE_STRING);		$document = filter_var($_BODY['document'], FILTER_SANITIZE_STRING);	$query = "UPDATE required_document SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",document='" . mysql_real_escape_string($document) . "'";	$query .= " WHERE id = '" . $required_document_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $required_document_id;	$F['service_id'] = $service_id;	$F['document'] = $document;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>