<?php$route = '/services/:service_id/service-area/';$app->post($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);		if(isset($clean['service_area']))		{		$service_area_id = getGUID();				$service_area = $clean['service_area'];		$description = $clean['description'];		$query = "INSERT INTO service_area(";					$query .= "id,";		$query .= "service_id,";		$query .= "service_area,";		$query .= "description";		$query .= ") VALUES(";				$query .= "'" . mysql_real_escape_string($service_area_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($service_area) . "',";		$query .= "'" . mysql_real_escape_string($description) . "'";		$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();			$F['id'] = $service_area_id;		$F['service_id'] = $service_id;		$F['service_area'] = $service_area;			$F['description'] = $description;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>