<?php$route = '/services/:service_id/service-area/:service_area_id/';$app->delete($route, function ($service_id,$service_area_id)  use ($app){	$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);		$service_area_id = filter_var($service_area_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM service_area WHERE service_id = '" . $service_id . "' AND id = '" . $service_area_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Service Area Deleted";	$ReturnObject['id'] = $service_area_id;	$ReturnObject['service_id'] = $service_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>