<?php$route = '/services/:service_id/service-area/:service_area_id/';$app->put($route, function ($service_id,$service_area_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$service_id = filter_var($service_id, FILTER_SANITIZE_STRING);		$service_area_id = filter_var($service_area_id, FILTER_SANITIZE_STRING);		$service_area = filter_var($_BODY['service_area'], FILTER_SANITIZE_STRING);		$description = filter_var($_BODY['description'], FILTER_SANITIZE_STRING);		$query = "UPDATE service_area SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",id='" . mysql_real_escape_string($service_area_id) . "'";	$query .= ",service_area='" . mysql_real_escape_string($service_area) . "'";	$query .= ",description='" . mysql_real_escape_string($description) . "'";	$query .= " WHERE id = '" . $service_area_id . "'";	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();		$F['id'] = $service_area_id;	$F['service_id'] = $service_id;	$F['service_area'] = $service_area;	$F['description'] = $description;			$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>